// Assuming the previous node's output is an array of devices
const devices = $input.first().json.devices;
const targetDevice = devices.find(device => device.name === "Samarth's Echo Dot");

if (targetDevice) {
  if (!targetDevice.is_active) {
    // Device is not active, proceed to start playback
    return [{ json: { device_id: targetDevice.id, shouldPlay: true } }];
  } else {
    // Device is active, no need to start playback
    return [{ json: { device_id: targetDevice.id, shouldPlay: false } }];
  }
} else {
  // Device not found
  return [{ json: { error: "Device not found" } }];
}

// Create a Set to track unique track IDs
const uniqueTracks = new Set();

return $input.all()
  .filter(item => item.json.track !== null)
  .filter(item => item.json.track.is_playable == true )
  .filter(item => {
    const trackId = item.json.track.id;
    if (uniqueTracks.has(trackId)) {
      return false; // Skip duplicate track
    }
    uniqueTracks.add(trackId);
    return true;
  })
  .map(item => {
    const track = item.json.track;
    return {
      json: {
        id: Math.floor(Math.random()*100000), 
        content: `"${track.name}" by "${track.artists.map(a => a.name).join(", ")}" from album "${track.album.name}"`,
        metadata: {
          title: track.name,
          artists: track.artists.map(a => a.name),
          album: track.album.name,
          added_at: item.json.added_at,
          uri: 'spotify:track:'+track.id,
          type: "track"
        }
      }
    };
   });

